import React from "react";
import {Map, GoogleApiWrapper, Marker} from "google-maps-react"; /* , Marker*/
import { useState, useEffect } from "react";

function RentalsMap({locations, google, setHighLight }) { /*, setHighLight*/

  const [center, setCenter] = useState();

  useEffect(() => {
    var arr = Object.keys(locations);
    var getLat = (key) => locations[key]["lat"];
    var avgLat = arr.reduce((a, c) => a + Number(getLat(c)), 0) / arr.length;
    
    var getLng = (key) => locations[key]["lng"];
    var avgLng = arr.reduce((a, c) => a + Number(getLng(c)), 0) / arr.length;

    setCenter({lat: avgLat, lng: avgLng});

  }, [locations]);

  return (
    <>
      {center && (
        <Map 
        google = {google}
        containerStyle = {{
          width: "50vw",
          height: "calc(100vh - 135px)",
        }}
        center = {center}
        initialCenter = {locations[0]}
        zoom = {13}
        disableDefaultUI = {true}
      >
         { locations.map((coords, i) => (
          <Marker position = {coords} onClick = {() => setHighLight(i)} />
        ))} }
      </Map>
      )}
    </>
  );
}

export default GoogleApiWrapper({
  apiKey: "AIzaSyAhp37ovli2YMXo6gxLQK2z8ZG91S3SgXM"
}) (RentalsMap);
